function whatDoesItDo(arr) {
  const n = arr.length;
  const stack = [];
  for (let i = 0; i < n; i++) {
    while (stack.length > 0 && arr[stack[stack.length - 1]] > arr[i]) {
      stack.pop();
    }
    stack.push(i);
  }
  return stack;
}
```
