function solve(n, a, b, c) {
  if (n == 0) return 0;
  if (n < 0) return -Infinity;

  const res1 = solve(n - a, a, b, c);
  const res2 = solve(n - b, a, b, c);
  const res3 = solve(n - c, a, b, c);

  return 1 + Math.max(res1, res2, res3);
}
```
